export function fixingHeader() {
    /* Varriables */
    
    const header = $('.header');
    let fakeHeader;
    const headerHeight = parseInt(header.height()) + parseInt(header.css('padding-top')) + parseInt(header.css('padding-bottom'));
    if ($('.fake-header').length == 0) {
        fakeHeader = $('<div class="fake-header"></div>')[0];
        $(fakeHeader).height(headerHeight);
    }
    
    /* Event handler */
    
    if ($(window).scrollTop() > headerHeight) {
        if ($('.fake-header').length == 0)
            $('body').prepend(fakeHeader);
        header.addClass('fixedHeader');
    }
    else {
        if ($('.fake-header').length != 0)
            $('.fake-header').remove();
        header.removeClass('fixedHeader');
    }
}