export function send(e, name, email, message) {
    $.ajax({
        url: './../php/mail.php',
        type: 'POST',
        cache: false,
        dataType: 'html',
        data: {
            name: name.val(),
            email: email.val(),
            message: message.val()
        },
        beforeSend: function() {
            $(e.target).attr('disabled', true);
        },
        success: function(data) {
            /* Varriables */
            
            const feedbackModalWin = $('.thx-for-feedback');
            let sec = 0;
            
            /* Code */
            
            $(e.target).attr('disabled', false);
            
            name.val('');
            email.val('');
            message.val('');
            
            feedbackModalWin.css('display', 'flex');
            feedbackModalWin.animate({
                height: '200px',
                padding: '25px'
            }, 400);
            
            setTimeout(() => {
                feedbackModalWin.animate({
                    height: 0,
                    padding: '0 25px'
                }, 400);
                
                setTimeout(() => feedbackModalWin.hide(), 400);
            }, 2000);
        }
    });
}