import { hideBurgerMenu } from './toggleBurgerMenu.min.js';

export function smoothScrolling(element, e) {
    e.preventDefault();
    
    /* Varriables */
    
    const burgerMenu = $('.burger-menu');
    const href = $(element).attr('href');
    const scrollTo = $(href);
    const headerHeight = $('.header').height() + 15 + 15;
    const y = scrollTo.offset().top - headerHeight;
    
    /* Code */
    
    hideBurgerMenu(burgerMenu);
    
    setTimeout(() => $('html, body').animate({
        'scrollTop': y + 'px'
    }, 400), 500);
}