/* Import */

import { ebleScroll } from './toggleScroll.min.js';
import { disableScroll } from './toggleScroll.min.js';

/* Show burger menu */

export function showBurgerMenu(menu) {
    if ($(window).width() > 1340) {
        menu.animate({
            width: '30%',
            padding: '100px 50px 200px 50px'
        }, 400);
        
        setTimeout(() => disableScroll(), 400);
    } else {
        if ($(window).width() > 700) {
            menu.animate({
                width: '100%',
                padding: '100px 50px 200px 50px'
            }, 400);
        } else {
            menu.animate({
                width: '100%',
                padding: '75px 50px 125px 50px'
            }, 400);
        }
        setTimeout(() => disableScroll(), 400);
    }
}

/* Hide burger menu */

export function hideBurgerMenu(menu) {
    if ($(window).width() > 1340) {
        ebleScroll();
        
        menu.animate({
            width: 0,
            padding: '100px 0 200px 0'
        }, 400);
    } else {
        ebleScroll();
        
        if ($(window).width() > 700) {
            menu.animate({
                width: '0',
                padding: '100px 0 200px 0'
            }, 400);
        } else {
            menu.animate({
                width: '0',
                padding: '75px 0 125px 0'
            }, 400);
        }
    }
}