/* Import */

import { ebleScroll } from './toggleScroll.min.js';
import { disableScroll } from './toggleScroll.min.js';

/* Show mdoal window */

export function showModalWin(bg, menu) {
    if ($(window).width() > 1340) {
        disableScroll();
        
        bg.fadeIn();
        
        setTimeout(() => {
            bg.css('display', 'flex');
            
            menu.animate({
                height: '770px',
                padding: '56px'
            }, 400);
        }, 400);
    } else {
        disableScroll();
        
        bg.css('display', 'flex');

        menu.animate({
            width: '100%',
            padding: '56px'
        }, 400);
    }
}

/* Hide modal window */

export function hideModalWin(bg, menu) {
    if ($(window).width() > 1340) {
        menu.animate({
            height: '0',
            padding: '0 56px'
        }, 400);

        setTimeout(() => { bg.fadeOut(); ebleScroll(); }, 400);
    } else {
        menu.animate({
            width: '0',
            padding: '56px 0'
        }, 400);
        
        setTimeout(() => { bg.hide(); ebleScroll(); }, 400);
    }
}