/* Import */

import { fixingHeader } from './fixingHeader.min.js';

/* Disable scroll on the page */

export function disableScroll() {
    /* Varriables */
    
    const currentX = $(window).scrollLeft();
    const currentY = $(window).scrollTop();
    
    /* Event handlers */
    
    $(window).on('scroll', () => window.scrollTo(currentX, currentY));
    $(window).on('touchmove', (e) => e.preventDefault());
}

/* Eble scroll on the page */

export function ebleScroll() {
    /* Event handlers */
    
    $(window).off('scroll');
    $(window).off('touchmove');
    $(window).on('scroll', fixingHeader);
}